mapper.select:
	select  rownum, num, title, input_date, ip, cnt, id
	from ( select  rownum, num, title, input_date, ip, cnt, id ,
	row_number() over(order by input_date desc) rnum
	from board



	  	instr(${fieldStr}, #{keyword}) != 0


  ) where rnum between #{startNum} and #{endNum}

select  title, content , input_date, ip, cnt, id
from board 
where num = #{num}

mapper.update:
update board
set cnt=cnt+1 
where num = ${num}

update board
set content=#{content}, ip=#{ip}
where num=#{num} and id=#{id}
